definition.script:
pipeline {
  agent any
  stages {
    stage ("Cleanup") {
      agent {
        label "aix&&18.0&&cita_latest"
      }
      steps {
        catchError(buildResult: "UNSTABLE", stageResult: "FAILURE") {
          sh "/opt/mdyalog/18.0/64/unicode/p8/mapl load=/devt/apltools/CITA/CITA-Tests/cleanup.aplf userconfigfile=/devt/apltools/CITA/CITA-Tests/cleanup.dcfg"
          //sh "exit 0"  // end this step w/o problem-status, as it would avoid execution of next steps...
        }
      }
    }
    stage ("Run Tests") {
      parallel {
        %STAGES%  
      }
    }
  }
  post {// Clean up
    always {
        cleanWs()
        script {
          sh "ls $JENKINS_HOME/jobs/$JOB_NAME/builds/lastSuccessfulBuild/log"
          def jenkins = Jenkins.getInstance()
          def job = jenkins.getItem(jobName)
          def bld = job.getBuildByNumber(buildNumber)
          //use the method that suits you
          //bld.getLog(100) //number of lines to read
          bld.getLogFile()
          //bld.getLogReader() 
        }
    }
  }
}
