definition.script:
pipeline {
  agent any
  options { 
    disableConcurrentBuilds() 
  }
  stages {
    stage ("Cleanup") {
      agent {
        label "aix&&18.2&&cita_latest"
      }
      steps {
        cleanWs()
        catchError(buildResult: "UNSTABLE", stageResult: "FAILURE") {
          script {
            def citaDEVT = "/devt/"
            sh "/opt/mdyalog/18.2/64/unicode/p9/mapl load=/devt/apltools/CITA/CITA-Tests/cleanup.aplf configfile=/devt/apltools/CITA/CITA-Tests/cleanup.dcfg FOLDER=%xinD%.."                             
            sh "exit 0"  // end this step w/o problem-status, as it would avoid execution of next steps...
          }
        }
      }
    }
    stage ("Run Tests") {
      parallel {
        %STAGES%  
      }
    }
    stage ("Unlock") {
      agent {
        label "aix&&18.2&&cita_latest"
      }
      steps {
        script {
          catchError(buildResult: "UNSTABLE", stageResult: "FAILURE") {
            def citaDEVT = "/devt/"
            // permissions for everything in the test's folder
            // no longer needed...
            // sh "find %xinD% -user $USER -exec chmod -R 755 {} +"
            sh "find %xinD% -user jenkins -exec chmod -R 755 {} \\;"
            // and now delete the lockfile
            def LOCKFILE="%LOCKFILE%"
            if (LOCKFILE != "") {
              sh "rm $LOCKFILE"
            }
          }
        } 
      }
    }
  }
}
